mapper.select:
select t.day,a.app_name,h.channel_name,q.name as cn_name, case when q.cpa_price>0 then round(t.all_fee*q.fee_percent*(100-q.reduce_percent)/(10000*q.cpa_price),0) else round(t.all_fee*q.fee_percent*(100-q.reduce_percent)/(10000),0) end as activates from ( select to_char(s.order_dt,'yyyy-mm-dd') day,s.channel_id,s.cn_app_id,sum(s.fee_value) all_fee from v_sync_info s where status=0 and to_char(s.order_dt,'yyyy-mm-dd') between #{findDto.startDate,jdbcType=VARCHAR} and #{findDto.endDate,jdbcType=VARCHAR} and s.app_id!=407
and s.channel_id=#{findDto.channelId,jdbcType=INTEGER}
and s.cn_app_id=#{findDto.appId,jdbcType=INTEGER}
group by to_char(s.order_dt,'yyyy-mm-dd'),s.channel_id,s.cn_app_id ) t inner join T_APP a on a.id=t.cn_app_id inner join T_CHANNEL h on h.id=t.channel_id inner join T_CHANNEL_APP q on t.cn_app_id=q.app_id and t.channel_id=q.channel_id order by t.day asc,t.channel_id asc
