and to_char(r.daytime,'yyyy-mm-dd')
between
#{findDto.startDate,jdbcType=VARCHAR} and 
#{findDto.endDate,jdbcType=VARCHAR}
)tb
)t
where t.rm between
#{page.pageStartIndex} and #{page.pageEndIndex}
