mapper.select:
select t.*,c.name as channelAppName,c.reduce_percent from ( select rownum rm,tb.* from ( select sma.settlement_id as ds_js,smca.settlement_id as dx_js,cpin.cp_name,ch.channel_name,ap.app_name,v.channel_app_id,fee_value,count(distinct v.mobile) as total_users,count(*) as up_count, sum(decode(v.status,null,0,1)) as down_count, sum(decode(v.status,1,1,0)) as suc_count, sum(decode(v.reduce_status,1,1,0)) as reduce_count,sum(decode(v.sync_status,1,1,0)) as synced_count from T_COUNT_INCOME v inner join t_channel_app ca on ca.id=v.channel_app_id inner join t_channel ch on ch.id=ca.channel_id inner join t_app ap on ap.id=ca.app_id inner join t_cp_info cpin on cpin.id=ap.cp_id inner join t_settlement_app sma on sma.app_id=ap.id inner join t_settlement_channel_app smca on smca.channel_app_id=ca.id
and v.channel_app_id=#{findDto.channelAppId,jdbcType=INTEGER}
and ca.channel_id=#{findDto.channelId,jdbcType=INTEGER}
and ap.id=#{findDto.appId,jdbcType=INTEGER}
and cpin.id=#{findDto.spId,jdbcType=INTEGER}
and sma.settlement_id=#{findDto.dsjs,jdbcType=INTEGER}
and smca.settlement_id=#{findDto.dxjs,jdbcType=INTEGER}
and v.reduce_status=0
and to_char(v.createtime,'yyyy-mm-dd') between #{findDto.startDate,jdbcType=VARCHAR} and #{findDto.endDate,jdbcType=VARCHAR}
where v.channel_app_id is not null group by v.channel_app_id,fee_value,ch.channel_name,ap.app_name,cpin.cp_name,sma.settlement_id,smca.settlement_id order by v.channel_app_id,fee_value ) tb )t inner join t_channel_app c on c.id=t.channel_app_id where rm between #{page.pageStartIndex} and #{page.pageEndIndex} order by t.channel_app_id

select count(distinct v.channel_app_id||v.fee_value)
from T_COUNT_INCOME v
where channel_app_id is not null

	and v.channel_app_id=#{findDto.channelAppId,jdbcType=INTEGER}


	and v.reduce_status=0

and to_char(v.createtime,'yyyy-mm-dd') between
#{findDto.startDate,jdbcType=VARCHAR}
and #{findDto.endDate,jdbcType=VARCHAR}

select t.* from (
select rownum rm,tb.* from (
select v.channel_app_id,fee_value, v.mobile,c.name as channelAppName,
decode(v.status,null,0,1) as down_status, v.status as fee_status,
v.reduce_status,v.sync_status,v.createtime,v.province,c.channel_id
from T_COUNT_INCOME v
inner join t_channel_app c on c.id=v.channel_app_id
where channel_app_id is not null

	and c.channel_id=#{findDto.channelId,jdbcType=INTEGER}


	and v.channel_app_id=#{findDto.channelAppId,jdbcType=INTEGER}


	and v.reduce_status=0


	and v.mobile=#{findDto.mobile,jdbcType=INTEGER}

and to_char(v.createtime,'yyyy-mm-dd') between
#{findDto.startDate,jdbcType=VARCHAR} and
#{findDto.endDate,jdbcType=VARCHAR}
order by v.channel_app_id desc ,v.createtime desc
) tb
)t where rm between #{page.pageStartIndex} and #{page.pageEndIndex}

select count(*) from T_COUNT_INCOME v inner join t_channel_app c on
c.id=v.channel_app_id
where channel_app_id is not null

	and v.channel_app_id=#{findDto.channelAppId,jdbcType=INTEGER}


	and c.channel_id=#{findDto.channelId,jdbcType=INTEGER}


	and v.reduce_status=0


	and v.mobile=#{findDto.mobile,jdbcType=INTEGER}

and to_char(v.createtime,'yyyy-mm-dd') between
#{findDto.startDate,jdbcType=VARCHAR} and
#{findDto.endDate,jdbcType=VARCHAR}

select * from t_channel_app where id in(
select channel_app_id from T_COUNT_INCOME
)
